// Texto del cuerpo limpio
const texto = $json.cuerpo_limpio || "";

// Buscar campos clave
const nombreMatch = texto.match(/Nombre y apellido:\s*([A-Za-z√Å√â√ç√ì√ö√ë√°√©√≠√≥√∫√º√ú\s]+)/i);
const telefonoMatch = texto.match(/Tel[e√©]fono:\s*(\d{10,15})/i);
const correoMatch = texto.match(/E-?mail:\s*([\w\.-]+@[\w\.-]+\.\w+)/i);
const codigoAvisoMatch = texto.match(/C√≥digo de aviso:\s*([A-Z0-9]+)/i);

// Detectar bloque del aviso (Renta o Venta)
const avisoMatch = texto.match(
  /aviso:\s*((?:Renta|Venta)\s*\|[^]+?)(?=(C√≥digo de aviso:|C√≥digo del anunciante:|Este es el mensaje))/i
);

let aviso = avisoMatch ? avisoMatch[1].trim() : null;

if (aviso) {
  // Limpiar texto y eliminar repeticiones de encabezado
  aviso = aviso
    .replace(/¬°Hola, Inmobiliaria Villanueva Garc√≠a! Hay interesados que est√°n haciendo consultas por el siguiente aviso:/gi, "")
    .replace(/\s+/g, " ")
    .trim();

  // Extraer solo la parte DESPU√âS del primer pipe "|"
  const innerMatch = aviso.match(/^\s*(?:Renta|Venta)\s*\|\s*(.*)$/i);
  if (innerMatch) aviso = innerMatch[1].trim();

  // Opcional: eliminar fragmentos irrelevantes como ‚ÄúVer aviso‚Äù
  aviso = aviso.replace(/Ver aviso/gi, "").trim();
}

// Normalizar tel√©fono (quitar prefijo 52 si lo tiene)
let telefono = telefonoMatch ? telefonoMatch[1].trim() : null;
if (telefono?.startsWith("52") && telefono.length === 12) {
  telefono = telefono.slice(2);
}

// Generar wa_id con formato internacional 521XXXXXXXXXX
const wa_id = telefono ? `521${telefono}` : null;

// Crear objeto final
return [{
  nombre: nombreMatch ? nombreMatch[1].trim().replace(/Tel[e√©]fono/i, "").trim() : null,
  telefono,
  wa_id,
  correo: correoMatch ? correoMatch[1].trim() : null,
  aviso,
  codigo_aviso: codigoAvisoMatch ? codigoAvisoMatch[1].trim() : null,
  tipo: texto.includes("Renta |") ? "renta" : texto.includes("Venta |") ? "venta" : null
}];

// Obtener el HTML del campo actual
let raw = $json.html || "";

// Limpiar etiquetas HTML, estilos, scripts y espacios
let clean = raw
  .replace(/<style[\s\S]*?<\/style>/gi, "")  // Quita CSS embebido
  .replace(/<script[\s\S]*?<\/script>/gi, "") // Quita scripts
  .replace(/<[^>]+>/g, " ")                  // Quita todas las etiquetas HTML
  .replace(/&nbsp;/gi, " ")                  // Reemplaza espacios especiales
  .replace(/&[a-z]+;/gi, "")                 // Quita entidades HTML (&lt;, &gt;, etc.)
  .replace(/\s+/g, " ")                      // Limpia espacios y saltos m√∫ltiples
  .trim();

// Retornar el texto limpio
return [{
  cuerpo_limpio: clean || "(sin contenido detectado)",
  longitud: clean.length
}];

// ‚úÖ Acceder de forma segura al primer item de cada nodo
const apiContact = $('Get API Contact').first()?.json ?? {};
const contextStatus = $('Check Context Status').first()?.json ?? {};

// üß† Validar si vienen vac√≠os o con data √∫til
const isApiEmpty =
  !apiContact ||
  Object.keys(apiContact).length === 0 ||
  (apiContact.data && Array.isArray(apiContact.data) && apiContact.data.length === 0);

const isContextEmpty =
  !contextStatus ||
  Object.keys(contextStatus).length === 0 ||
  (Array.isArray(contextStatus) && contextStatus.length === 0) ||
  (contextStatus.data && Array.isArray(contextStatus.data) && contextStatus.data.length === 0);

// ‚úÖ Si ambos est√°n vac√≠os => nuevo contacto
const isNew = isApiEmpty && isContextEmpty;

// üîÑ Retornar resultado para usar en el siguiente IF
return [{
  is_new: isNew,
  api_empty: isApiEmpty,
  context_empty: isContextEmpty,
  api_data: apiContact,
  context_data: contextStatus
}];

const extractData = $('Extract: data').item.json;
const aiMessage = $('Message a model').item.json.message.content;

// Escapar comillas simples
const escapeQuotes = (str) => {
  if (!str) return '';
  return str.replace(/'/g, "''");
};

return {
  wa_id: extractData.wa_id,
  nombre: escapeQuotes(extractData.nombre),
  telefono: extractData.telefono,
  correo: escapeQuotes(extractData.correo),
  aviso: escapeQuotes(extractData.aviso),
  tipo: extractData.tipo,
  mensaje_enviado: escapeQuotes(aiMessage)
};

const extractData = $('Extract: data').item.json;
const aiData = $('Message a model').item.json;
const apiContactData = $('Create API Contact').item.json;

const mensaje = aiData.message.content;

const comment = `üìß CONTACTO V√çA EMAIL INMUEBLES24

üìã INFORMACI√ìN:
Tipo: ${extractData.tipo}
Aviso: ${extractData.aviso}

üí¨ MENSAJE ENVIADO POR WHATSAPP:
${mensaje}

‚úÖ Estado: Esperando respuesta del contacto`;

return {
  api_contact_id: apiContactData.data.id,
  comment: comment
};
