  ? contextData.interacciones.map((entry) => ({ ...entry }))
  : [];
const interactionEntry = {
  timestamp,
  actor: 'usuario',
  canal: 'whatsapp',
  tipo: 'mensaje_recibido',
  mensaje: originalMessage,
  origen: 'auto_filter',
  detalles: {
    motivo: reasonText,
    palabras_detectadas: matchedWords
  }
};
const updatedInteractions = [...existingInteractions, interactionEntry];

// ‚úÖ Motivos actualizados
const existingReasons = Array.isArray(contextData.motivo_rechazo)
  ? contextData.motivo_rechazo
  : [];
const updatedReasons = [...existingReasons];
if (!updatedReasons.includes(reasonText)) updatedReasons.push(reasonText);

