const apiResponse = $json;
const whatsappData = $('WhatsApp Trigger').item.json || {};

const tryParseJson = (value) => {
  if (typeof value !== 'string') return null;
  try {
    return JSON.parse(value);
  } catch (error) {
    console.log('No se pudo convertir el valor a JSON:', error.message);
    return null;
  }
};

const looksLikeContact = (value) => {
  if (!value || typeof value !== 'object') return false;
  const keys = Object.keys(value);
  const hints = [
    'id',
    'uuid',
    'contact_id',
    'telefono',
    'phone',
    'nombre',
    'status',
    'estado',
    'rechazo_previo',
    'resumen_interacciones',
    'etiquetas'
  ];
  return hints.some((hint) => keys.includes(hint));
};

const extractContact = (value) => {
  if (!value) return null;
  if (typeof value === 'string') {
    const parsed = tryParseJson(value);
    return parsed ? extractContact(parsed) : null;
  }
  if (Array.isArray(value)) {
    for (const item of value) {
      const candidate = extractContact(item);
      if (candidate) return candidate;
    }
    return null;
  }
  if (typeof value === 'object') {
    if (looksLikeContact(value)) {
      return value;
    }

    const nestedKeys = ['data', 'result', 'payload', 'body', 'contact', 'contacto'];
    for (const key of nestedKeys) {
      if (key in value) {
        const candidate = extractContact(value[key]);
        if (candidate) return candidate;
      }
    }
  }
  return null;
};

const normalizeBoolean = (value) => {
  if (typeof value === 'boolean') return value;
  if (typeof value === 'number') return value !== 0;
  if (typeof value === 'string') {
    const normalized = value.trim().toLowerCase();
    if (!normalized) return false;
    return [
      'true',
      '1',
      'yes',
      'si',
      's√≠',
      'rechazado',
      'rejected',
      'blocked',
      'bloqueado'
    ].includes(normalized);
  }
  if (Array.isArray(value)) {
    return value.some((entry) => normalizeBoolean(entry));
  }
  if (value && typeof value === 'object') {
    return Object.values(value).some((entry) => normalizeBoolean(entry));
  }
  return false;
};

const getFirstString = (...values) => {
  for (const value of values) {
    if (typeof value === 'string' && value.trim()) {
      return value.trim();
    }
  }
  return '';
};

