// ===================================================
// NODE: Parse AI JSON
// ===================================================
// Este nodo toma la respuesta cruda de la IA (por ejemplo, del nodo "AI Agent")
// y la convierte en datos estructurados.
// Espera un JSON en formato:
// [
//   {
//     "message": "...",
//     "contexto": "...",
//     "requiereAtencion": true,
//     "timestamp": "2025-11-06T22:13:00Z"
//   }
// ]
// ===================================================

const raw = $json.output || $json.text || $json.message || '';

let parsed = [];

try {
  // El modelo a veces devuelve texto con espacios o saltos de l√≠nea,
  // as√≠ que limpiamos y detectamos el bloque JSON.
  const jsonMatch = raw.match(/\[.*\]/s);
  if (jsonMatch) {
    parsed = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error('No se encontr√≥ un arreglo JSON v√°lido en la respuesta.');
  }
} catch (err) {
  return [
    {
      json: {
        error: true,
        message: 'Error al parsear la salida de la IA',
        detalle: err.message,
        raw_output: raw,
      },
    },
  ];
}

// Si todo sali√≥ bien, devolvemos cada elemento del arreglo como un item nuevo
return parsed.map(entry => ({
  json: {
    message: entry.message || '',
    contexto: entry.contexto || '',
    requiereAtencion: entry.requiereAtencion ?? false,
    timestamp: entry.timestamp || new Date().toISOString(),
  },
}));

