const detectorData = $('Parse Misbehavior').item.json;
const contextRecord = $json.context_record || null;

// ‚úÖ Evitar error si context_data no existe
const contextData = contextRecord?.context_data
  ? JSON.parse(JSON.stringify(contextRecord.context_data))
  : {};

// ‚úÖ Verificar si ya existe contexto
const isExisting = Boolean(contextRecord && contextRecord.id);

// ‚úÖ Obtener contacto de manera segura
const contactData = $('Get API Contact').first().json?.data || [];
const contactName = contactData[1]?.nombre || contactData[0]?.nombre || null;

// ‚úÖ Nombre con fallback seguro
const fallbackName =
  contactName && contactName.trim() !== ''
    ? contactName.trim()
    : 'ü§ñ - Sin Nombre Mal Comportamiento';

// ‚úÖ Texto de motivo de rechazo
const reasonText =
  detectorData.misbehaviorReason && detectorData.misbehaviorReason.trim() !== ''
    ? detectorData.misbehaviorReason
    : 'Comportamiento indebido detectado autom√°ticamente';

// ‚úÖ Palabras y mensaje original
const matchedWords = detectorData.misbehaviorMatchedWords || [];
const originalMessage = detectorData.misbehaviorOriginalMessage || detectorData.userPrompt || '';
const timestamp = new Date().toISOString();

// ‚úÖ Registro de historial
const historyEntry = {
  timestamp,
  mensaje_original: originalMessage,
  palabras_detectadas: matchedWords,
  origen: 'auto_filter'
};

// ‚úÖ Motivos actualizados
const existingReasons = Array.isArray(contextData.motivo_rechazo)
  ? contextData.motivo_rechazo
  : [];
const updatedReasons = [...existingReasons];
if (!updatedReasons.includes(reasonText)) updatedReasons.push(reasonText);

// ‚úÖ Historial actualizado
const existingHistory = Array.isArray(contextData.mal_comportamiento_historial)
  ? contextData.mal_comportamiento_historial
  : [];
const updatedHistory = [...existingHistory, historyEntry];

// ‚úÖ Nuevo contexto actualizado
const updatedContext = {
  ...contextData,
  nombre: fallbackName,
  status: 'blocked',
  rechazo_previo: true,
  motivo_rechazo: updatedReasons,
  mal_comportamiento_historial: updatedHistory,
  ultima_interaccion: timestamp
};

// ‚úÖ Resultado final
return [
  {
    json: {
      ...detectorData,
      misbehaviorOriginalMessage: originalMessage,
      isExistingContext: isExisting,
      contextId: contextRecord?.id || null,
      updatedContext,
      misbehaviorHistoryEntry: historyEntry,
      api_contact_id: contextData?.api_contact_id || null,
      needsApiContact: !contextData?.api_contact_id,
      fallbackName,
      reasonText
    }
  }
];

