const contextData = input.context_data ? JSON.parse(JSON.stringify(input.context_data)) : {};

const reasonRaw = input.misbehaviorReason;
const reasonText = typeof reasonRaw === 'string' && reasonRaw.trim() !== ''
  ? reasonRaw.trim()
  : 'Comportamiento indebido detectado autom√°ticamente';

const matchedWords = Array.isArray(input.misbehaviorMatchedWords)
  ? input.misbehaviorMatchedWords.filter(Boolean)
  : [];
const originalMessage = typeof input.userPrompt === 'string' ? input.userPrompt.trim() : '';
const timestamp = new Date().toISOString();

const historyEntry = {
  timestamp,
  mensaje_original: originalMessage,
  palabras_detectadas: matchedWords,
  origen: 'auto_filter'
};

const existingHistory = Array.isArray(contextData.mal_comportamiento_historial)
  ? [...contextData.mal_comportamiento_historial]
  : [];
existingHistory.push(historyEntry);

const existingReasons = Array.isArray(contextData.motivo_rechazo)
  ? [...contextData.motivo_rechazo]
  : [];
if (!existingReasons.includes(reasonText)) {
  existingReasons.push(reasonText);
}

const updatedContext = {
  ...contextData,
  status: 'blocked',
  rechazo_previo: true,
  motivo_rechazo: existingReasons,
  mal_comportamiento_historial: existingHistory,
  ultima_interaccion: timestamp
};

if (!updatedContext.api_contact_id && contextData.api_contact_id) {
  updatedContext.api_contact_id = $('Get API Contact1').first().json.data[0].id;
}

const commentLines = [
  'üö´ IGNORADO POR BLOQUEO üö´',
  ` Motivo: ${reasonText}`,
  matchedWords.length ? ` Coincidencias: ${matchedWords.join(', ')}` : '',
  originalMessage ? ` Mensaje original: "${originalMessage}"` : '',
  ` Registrado el: ${timestamp}`
].filter((line) => line && line.trim() !== '');

const apiContactId = updatedContext.api_contact_id;

if (!apiContactId) {
  throw new Error('No se encontr√≥ api_contact_id en el contexto para registrar el comentario.');
}

return [{
  json: {
    wa_id: input.wa_id,
    api_contact_id: apiContactId,
    commentPayload: {
      comentario: commentLines.join('\n')
    },
    updatedContext,
    historyEntry
  }
}];
