const triggerData = $json;
const contextData = triggerData.context_data || {};

// Obtener cuestionario y paso actual
const questionnaire = contextData.questionnaire || [];
const currentStep = contextData.questionnaire_step || 1;
const totalQuestions = contextData.questionnaire_total || questionnaire.length;

// Obtener pregunta actual (los arrays empiezan en 0, los steps en 1)
const currentQuestion = questionnaire[currentStep - 1];

if (!currentQuestion) {
  throw new Error(`No se encontr√≥ la pregunta para el step ${currentStep}`);
}

console.log(`üìç Procesando pregunta ${currentStep} de ${totalQuestions}`);
console.log(`‚ùì Pregunta: ${currentQuestion.field}`);

return {
  wa_id: triggerData.wa_id,
  userPrompt: triggerData.userPrompt,
  context_data: contextData,
  api_contact: triggerData.api_contact,

  // Datos de la pregunta actual
  currentStep: currentStep,
  totalQuestions: totalQuestions,
  currentQuestion: currentQuestion,
  questionnaire: questionnaire
};

const data = $json;
const question = data.currentQuestion;
const userAnswer = data.userPrompt;

console.log(`üìù Capturando respuesta para: ${question.field}`);
console.log(`üí¨ Respuesta: "${userAnswer}"`);

// Simplemente capturamos la respuesta sin validar
// La IA har√° toda la validaci√≥n al final

return {
  ...data,
  isValid: true,  // Siempre true - la IA valida despu√©s
  parsedAnswer: userAnswer,
  errorMessage: ''  // Nunca hay error en esta fase
};

const data = $json;
const timestamp = new Date().toISOString();

// Clonar contexto
const updatedContext = JSON.parse(JSON.stringify(data.context_data));

// Agregar respuesta al array de respuestas
const answerEntry = {
  step: data.currentStep,
  field: data.currentQuestion.field,
  section: data.currentQuestion.section,
  question: data.currentQuestion.question,
  answer: data.parsedAnswer,
  passed: data.isValid,
  timestamp: timestamp
};

updatedContext.questionnaire_answers.push(answerEntry);

// Incrementar step
const nextStep = data.currentStep + 1;
updatedContext.questionnaire_step = nextStep;
updatedContext.ultima_interaccion = timestamp;

// Agregar interacci√≥n
const interactions = Array.isArray(updatedContext.interacciones) 
  ? updatedContext.interacciones 
  : [];

interactions.push({
  timestamp: timestamp,
  actor: 'usuario',
  canal: 'whatsapp',
  tipo: 'respuesta_cuestionario',
  mensaje: `Pregunta ${data.currentStep}: ${data.parsedAnswer}`,
  detalles: {
    field: data.currentQuestion.field,
    paso: data.currentStep,
    total: data.totalQuestions
  }
});

updatedContext.interacciones = interactions;

// Verificar si hay m√°s preguntas
const hasMoreQuestions = nextStep <= data.totalQuestions;

console.log(`üìä Progreso: ${data.currentStep}/${data.totalQuestions}`);
console.log(`‚û°Ô∏è Siguiente: ${hasMoreQuestions ? `Pregunta ${nextStep}` : 'Finalizar'}`);

return {
  wa_id: data.wa_id,
  updatedContext: updatedContext,
  currentStep: data.currentStep,
  nextStep: nextStep,
  totalQuestions: data.totalQuestions,
  hasMoreQuestions: hasMoreQuestions,
  nextQuestion: hasMoreQuestions ? data.questionnaire[nextStep - 1] : null,
  api_contact_id: updatedContext.api_contact_id
};

const data = $json;
const contextData = data.updatedContext;

// Obtener todas las respuestas
const answers = contextData.questionnaire_answers || [];
const questionnaire = contextData.questionnaire || [];

// Preparar datos para el AI
const evaluationData = {
  property: {
    title: contextData.property_found.title,
    restrictions: contextData.property_found.restrictions
  },
  questionnaire_with_answers: questionnaire.map((q, index) => {
    const answer = answers.find(a => a.field === q.field);
    return {
      field: q.field,
      section: q.section,
      question: q.question,
      type: q.type,
      expected: q.expected,
      answer_given: answer ? answer.answer : null,
      passed: answer ? answer.passed : null
    };
  }),
  contact_info: {
    nombre: contextData.nombre,
    telefono: contextData.telefono,
    wa_id: data.wa_id
  }
};

return {
  wa_id: data.wa_id,
  updatedContext: contextData,
  evaluationData: evaluationData,
  api_contact_id: contextData.api_contact_id
};

const prepareData = $('Prepare Evaluation Data').item.json;

// Parsear respuesta del AI - FIX: acceder correctamente al texto
let evaluationText = '';

try {
  // La estructura es: output[0].content[0].text
  evaluationText = $json.output[0].content[0].text || '';
} catch (error) {
  console.error('‚ùå Error accediendo al output de la IA:', error);
  evaluationText = '';
}

// Limpiar markdown si viene
evaluationText = evaluationText.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();

let evaluation = {
  status: 'rejected',
  rejection_reasons: ['Error al evaluar el perfil'],
  summary: 'Error en la evaluaci√≥n',
  recommendation: ''
};

try {
  evaluation = JSON.parse(evaluationText);
  console.log(`üìä Evaluaci√≥n: ${evaluation.status.toUpperCase()}`);

  if (evaluation.status === 'rejected') {
    console.log('‚ùå Motivos:', evaluation.rejection_reasons.join(', '));
  } else {
    console.log('‚úÖ Perfil aprobado');
  }

} catch (error) {
  console.error('‚ùå Error parseando evaluaci√≥n:', error);
  console.log('Texto recibido:', evaluationText.substring(0, 200));
}

return {
  wa_id: prepareData.wa_id,
  updatedContext: prepareData.updatedContext,
  api_contact_id: prepareData.api_contact_id,
  evaluation: evaluation,
  isApproved: evaluation.status === 'approved'
