const buildData = $('Build Rejection Context').item.json;
const nombre = buildData.nombre.split(' ')[0]; // Primer nombre

// Funci√≥n para limpiar t√©rminos t√©cnicos de los motivos
function cleanReason(reason) {
  return reason
    // Eliminar referencias t√©cnicas entre par√©ntesis
    .replace(/\(.*?(acepta_|tiene_|cumple_|tipo_).*?\)/gi, '')
    // Eliminar comillas simples con campos t√©cnicos
    .replace(/'.*?(acepta_|tiene_|cumple_|tipo_).*?'/gi, '')
    // Eliminar "contraviene"
    .replace(/contraviene/gi, '')
    // Limpiar espacios dobles
    .replace(/\s+/g, ' ')
    // Trim
    .trim()
    // Asegurar que empiece con may√∫scula
    .replace(/^./, str => str.toUpperCase())
    // Asegurar que termine con punto si no tiene
    .replace(/([^.])$/, '$1.');
}

// Limpiar motivos
const cleanedReasons = buildData.rejection_reasons.map(cleanReason);

// Mensaje amable y profesional
const message = `Gracias por tu tiempo ${nombre}, 

Lamentablemente este inmueble en particular no se ajusta a tu perfil debido a lo siguiente:

${cleanedReasons.map((reason, i) => `‚Ä¢ ${reason}`).join('\n')}

Te deseamos mucho √©xtio en tu busqueda, saludos, gracias por tu comprensi√≥n e inter√©s! üòä`;

console.log('üì® Mensaje de rechazo generado');
console.log('Motivos limpios:', cleanedReasons);

return {
