const triggerData = $('When Executed by Another Workflow').item.json;
const timestamp = new Date().toISOString();

// Clonar contexto existente
const updatedContext = JSON.parse(JSON.stringify(triggerData.context_data || {}));

// Cambiar status e iniciar cuestionario
updatedContext.status = "in_questionnaire";
updatedContext.questionnaire_step = 1;
updatedContext.ultima_interaccion = timestamp;

// Agregar interacci√≥n
const interactions = Array.isArray(updatedContext.interacciones) 
  ? updatedContext.interacciones 
  : [];

interactions.push({
  timestamp: timestamp,
  actor: 'usuario',
  canal: 'whatsapp',
  tipo: 'confirmacion_cuestionario',
  mensaje: triggerData['userPrompt '],
  detalles: {
    respuesta: 'afirmativa',
    inicia_cuestionario: true
  }
});

updatedContext.interacciones = interactions;

// Preparar primera pregunta basada en restricciones
const property = updatedContext.property_found || {};
const restrictions = property.restrictions || {};

let firstQuestion = "Perfecto, \n\n¬øCu√°ntas personas vivir√≠an en el inmueble?";

// Si la propiedad no acepta ni√±os, agregar aclaraci√≥n
if (restrictions.acepta_ni√±os === "no") {
  firstQuestion += "\n\n_(Solo adultos, la propiedad no acepta ni√±os)_";
}

return {
  wa_id: triggerData.wa_id,
  updatedContext: updatedContext,
  firstQuestion: firstQuestion,
  api_contact_id: updatedContext.api_contact_id
};

const parseData = $json;
const timestamp = new Date().toISOString();

// Clonar contexto existente
const updatedContext = JSON.parse(JSON.stringify(parseData.context_data || {}));

// Agregar cuestionario y configuraci√≥n
updatedContext.status = "in_questionnaire";
updatedContext.questionnaire = parseData.questionnaire;
updatedContext.questionnaire_step = 1;
updatedContext.questionnaire_total = parseData.total_questions;
updatedContext.questionnaire_answers = []; // Array vac√≠o para ir guardando respuestas
updatedContext.ultima_interaccion = timestamp;

// Agregar interacci√≥n al historial
const interactions = Array.isArray(updatedContext.interacciones) 
  ? updatedContext.interacciones 
  : [];

interactions.push({
  timestamp: timestamp,
  actor: 'sistema',
  canal: 'whatsapp',
  tipo: 'inicio_cuestionario',
  mensaje: 'Cuestionario generado e iniciado',
  detalles: {
    total_preguntas: parseData.total_questions,
    primera_pregunta: parseData.first_question.field
  }
});

updatedContext.interacciones = interactions;

// Preparar primera pregunta para WhatsApp
const firstQuestion = parseData.first_question;
let questionText = `Perfecto, empecemos üòä:\n\n${firstQuestion.question}`;

return {
  wa_id: parseData.wa_id,
  updatedContext: updatedContext,
  questionText: questionText,
  current_question: firstQuestion
};

const prepareData = $('Prepare Context with Questionnaire').item.json;

return {
  success: true,
  status: 'in_questionnaire',
  questionnaire_started: true,
  step: 1,
  total_questions: prepareData.updatedContext.questionnaire_total,
  wa_id: prepareData.wa_id,
  message_sent: true
};

// Obtener datos del nodo anterior
const previousData = $('Prepare Questionnaire Context').item.json;
const propertyData = previousData.updatedContext.property_found;
const contextData = previousData.updatedContext;

const TEMPLATE = {
  caracteristicas_fisicas: [
    {
      condition: () => !propertyData.estacionamientos || propertyData.estacionamientos === 0,
      field: "necesita_estacionamiento",
      question: "El inmueble no cuenta con estacionamiento. ¬øEst√°s de acuerdo con esto?"
    }
  ],

  restricciones_convivencia: [
    {
      condition: () => propertyData.restricciones?.acepta_ni√±os === "no",
      field: "tiene_ninos",
      question: "¬øEntre las personas que vivir√≠an en el inmueble habr√≠a ni√±os?"
    },
    {
      condition: () => propertyData.restricciones?.acepta_mascotas === "no",
      field: "tiene_mascotas",
      question: "El arrendador no acepta mascotas, es una restricci√≥n no negociable. ¬øTienes inconveniente con esto?"
    },
    {
      condition: () => propertyData.restricciones?.acepta_roomies === "no",
      field: "tiene_roomies",
      question: "Plat√≠came con quien vivir√°s ¬øsolo/a, con pareja, familia o con roomies?"
    }
  ],

  condiciones_contratacion: [
    {
      condition: () => propertyData.restricciones?.precio_poliza > 0,
      field: "acepta_poliza",
      question: `Se solicita una p√≥liza jur√≠dica de $${parseFloat(propertyData.restricciones.precio_poliza).toLocaleString('es-MX')} que se paga anualmente, es decir, cada a√±o que renueves el contrato. ¬øEst√°s de acuerdo?`
    },
    {
      condition: () => true,
      field: "acepta_deposito_renta",
      question: "Los pagos iniciales que se requieren son: Primer mes de renta y un mes de renta como dep√≥sito en garant√≠a. ¬øEst√°s de acuerdo con √©ste requisito?"
    }
  ],

  validacion_ingresos: [
    {
      condition: () => propertyData.restricciones?.ingresos_minimos > 0,
      field: "cumple_ingresos",
      question: `Se pide un ingreso m√≠nimo de $${parseFloat(propertyData.restricciones.ingresos_minimos).toLocaleString('es-MX')} mensuales, puede ser individual o mancomunado si vivir√°s en pareja. ¬øCumples con √©ste requisito?`
    },
    {
      condition: () => true,
      field: "ocupacion",
      question: "Para conocer mejor tu perfil, ¬øa qu√© te dedicas actualmente?"
    },
    {
      condition: () => propertyData.restricciones?.requiere_comprobantes_ingresos === true,
      field: "tipo_comprobante",
      question: "¬øQu√© tipo de comprobantes de ingresos tienes? (Recibo de n√≥mina timbrado por el SAT, estados de cuenta completos o facturas?)"
    }
  ]
};

// Generar cuestionario
const questionnaire = [];

for (const [section, questions] of Object.entries(TEMPLATE)) {
  questions.forEach(q => {
    if (q.condition()) {
      questionnaire.push({
        section: section,
        field: q.field,
        question: q.question,
        type: "text"
      });
    }
  });
}

// Obtener primera pregunta
const firstQuestion = questionnaire[0] || null;

// Validar que hay al menos una pregunta
if (!firstQuestion) {
  throw new Error('No se generaron preguntas para este inmueble');
}

return {
  wa_id: previousData.wa_id,
  questionnaire: questionnaire,
  total_questions: questionnaire.length,
  first_question: firstQuestion,    // ‚Üê ESTO FALTABA
  context_data: contextData          // ‚Üê ESTO FALTABA
};

const triggerData = $('When Executed by Another Workflow').item.json;
const timestamp = new Date().toISOString();

// Clonar contexto existente
const updatedContext = JSON.parse(JSON.stringify(triggerData.context_data || {}));

// Cambiar a "in_progress" con acci√≥n pendiente
updatedContext.status = "in_progress";
updatedContext.pending_action = "questionnaire_confirmation";
updatedContext.pending_property = updatedContext.property_found;
updatedContext.postponed_at = timestamp;
updatedContext.postponed_reason = "user_declined";
updatedContext.ultima_interaccion = timestamp;

// Agregar interacci√≥n
const interactions = Array.isArray(updatedContext.interacciones) 
  ? updatedContext.interacciones 
  : [];

interactions.push({
  timestamp: timestamp,
  actor: 'usuario',
  canal: 'whatsapp',
  tipo: 'cuestionario_pospuesto',
  mensaje: triggerData['userPrompt '],
  detalles: {
    respuesta: 'negativa',
    property_title: updatedContext.property_found?.title || 'inmueble'
  }
});

updatedContext.interacciones = interactions;

return {
  wa_id: triggerData.wa_id,
  updatedContext: updatedContext,
  property_title: updatedContext.property_found?.title || 'el inmueble'
};

return {
  success: true,
  action: 'questionnaire_postponed',
  wa_id: $('Handle Postponement').item.json.wa_id,
  new_status: 'in_progress',
  pending_action: 'questionnaire_confirmation',
  message_sent: true,
  bot_freed: true
};

const triggerData = $('When Executed by Another Workflow').item.json;
const timestamp = new Date().toISOString();

// Clonar contexto existente
const updatedContext = JSON.parse(JSON.stringify(triggerData.context_data || {}));

// MANTENER el status actual (para darle otra oportunidad)
// updatedContext.status sigue siendo "awaiting_questionnaire_confirmation"

updatedContext.ultima_interaccion = timestamp;

// Agregar interacci√≥n
const interactions = Array.isArray(updatedContext.interacciones) 
  ? updatedContext.interacciones 
  : [];

interactions.push({
  timestamp: timestamp,
  actor: 'usuario',
  canal: 'whatsapp',
  tipo: 'respuesta_ambigua_cuestionario',
  mensaje: triggerData['userPrompt '],
  detalles: {
    respuesta: 'ambigua',
    requiere_aclaracion: true
  }
});

updatedContext.interacciones = interactions;

return {
  wa_id: triggerData.wa_id,
  updatedContext: updatedContext,
  property_title: updatedContext.property_found?.title || 'el inmueble'
};

return {
  success: true,
  action: 'clarification_requested',
  wa_id: $('Handle Ambiguous Response').item.json.wa_id,
  status_maintained: 'awaiting_questionnaire_confirmation',
  message_sent: true,
  bot_freed: true,
  awaiting_clear_response: true
};
